package citiStrings;

public class DefaultValues {
	static int i;
	static float fl;
	static double d;
	static long l;
	static short sh;
	static byte b;
	static String s;
	static char c;
	static boolean bl;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
System.out.println("The default value of int:\s"+i);
System.out.println("The default value of float\s"+fl);
System.out.println("The default value of double\s"+d);
System.out.println("The default value of long\s"+l);
System.out.println("The default value of short\s"+sh);
System.out.println("The default value of byte\s"+b);
System.out.println("The default value of String\s"+s);
System.out.println("The default value of char\s"+c);
System.out.println("The default value of boolean\s"+bl);
	}

}
